import styled, { css } from "styled-components";

const subColor = "grey";
const mainColor = "black";

const shrinkLabel = css`
  top: -14px;
  font-size: 12px;
  color: ${mainColor};
`;

export const GroupContainer = styled.div`
  position: relative;
  margin: 45px 0;
`;
